<?phpinclude("db/db_connect.php");function round_calc($value){		$testval1 = explode(".",$value);	$whole = $testval1[0];	$decimal = $testval1[1];	if($decimal > 0)	{		$whole = $whole + 1;	}	return $whole; 	 }if(isset($_REQUEST['grosspay'])) { $grosspay = $_REQUEST['grosspay']; } else { $grosspay = ''; }$beforebasic = '';$basic = '';$nssfamount = '0.00';$nhifamount = '0.00';$payeecalc = '';$totaldeductions = '';$query71 = "select * from master_nssf where status <> 'deleted'";$exec71 = mysqli_query($GLOBALS["___mysqli_ston"], $query71) or die ("Error in Query71".mysqli_error($GLOBALS["___mysqli_ston"]));$res71 = mysqli_fetch_array($exec71);$res71componentname = $res71['componentname'];if($res71componentname != ''){	$nssfamount = $res71['amount'];}$taxableincome = $grosspay - $nssfamount;$query72 = "select * from master_nhif where status <> 'deleted'";$exec72 = mysqli_query($GLOBALS["___mysqli_ston"], $query72) or die ("Error in Query72".mysqli_error($GLOBALS["___mysqli_ston"]));while($res72 = mysqli_fetch_array($exec72)){	$res72from = $res72['from1'];	$res72to = $res72['to1'];	if($taxableincome >= $res72from && $taxableincome <= $res72to)	{		$nhifamount = $res72['amount'];	}}	$nhifamount;$nssfamount;$taxrelief = '0.00';$query55 = "select * from master_taxrelief where status <> 'deleted'";$exec55 = mysqli_query($GLOBALS["___mysqli_ston"], $query55) or die ("Error in Query55".mysqli_error($GLOBALS["___mysqli_ston"]));$res55 = mysqli_fetch_array($exec55);$res55finalamount = $res55['finalamount'];if($res55finalamount != ''){	$taxrelief = $res55finalamount;}//$taxableincome = $grosspay + $taxrelief + $nssfamount + $nhifamount;$taxableincome;$totalpercentcalc = '';$balance = '';$totalbalance = 0;$j = '0';$query73 = "select * from master_paye where status <> 'deleted'";$exec73 = mysqli_query($GLOBALS["___mysqli_ston"], $query73) or die ("Error in Query73".mysqli_error($GLOBALS["___mysqli_ston"]));while($res73 = mysqli_fetch_array($exec73)){	$res73from = $res73['from1'];	$res73to = $res73['to1'];	$res73percent = $res73['percent'];		if($taxableincome >= $res73from && $taxableincome <= $res73to)	{		$difference = $res73['difference'];		if($balance == "")		{			$payepercentcalc = $taxableincome * ($res73percent / 100);		}		else		{			$payepercentcalc = $balance * ($res73percent / 100);		}		$totalpercentcalc = $totalpercentcalc + $payepercentcalc;		$j = '1';	}	else	{		if($j == '0')		{			$difference = $res73['difference'];			if($balance == '')			{				$balance = $taxableincome - $difference;			}			else			{				$balance = $balance - $difference;			}						$payepercentcalc = $difference * ($res73percent / 100);			$totalpercentcalc = $totalpercentcalc + $payepercentcalc;		}	}	}$totalpercentcalc;$taxableincome;$payeecalc = $totalpercentcalc - $taxrelief;$payeecalc = number_format($payeecalc,2,'.','');$payeecalc = round_calc($payeecalc);$basic = $taxableincome + $nssfamount;$totaldeductions = $payeecalc + $nssfamount + $nhifamount;$nettpay = $basic - $totaldeductions;$basic = number_format($basic,2,'.',',');$payeecalc = number_format($payeecalc,2,'.',',');$nssfamount = number_format($nssfamount,2,'.',',');$nhifamount = number_format($nhifamount,2,'.',',');$totaldeductions = number_format($totaldeductions,2,'.',',');$nettpay = number_format($nettpay,2,'.',',');if($grosspay != ''){	echo $basic.'||'.$payeecalc.'||'.$nssfamount.'||'.$nhifamount.'||'.$totaldeductions.'||'.$nettpay.'';}?>